SELECT * FROM BOOKING;

SELECT * FROM (SELECT ROWNUM AS RNUM, B.* FROM (SELECT BOOKING_NO, MEMBER_NAME, EMAIL, PHONE, ROOM_NAME, CHECK_IN, CHECK_OUT, GUEST_ADULT, GUEST_CHILD, GUEST_INFANT, BOOKING_PRICE, BOOKING_COMMENT, BOOKING_STATE, PAYMENT_DATE FROM BOOKING JOIN MEMBER USING(MEMBER_NO) JOIN ROOM USING(ROOM_NO))B) WHERE RNUM BETWEEN 1 AND 10 AND CHECK_IN >= TRUNC(SYSDATE) - 7 AND CHECK_IN < TRUNC(SYSDATE) + 1;